// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol';

interface OptionBuyer is IERC721Receiver {

    function deposit(uint tokenId) external;
    function withdraw(uint tokenId) external;

}

contract Writter is ERC721Holder {

    IERC721 public token;
    OptionBuyer public optionBuyer;

    constructor(address _token, address _optionbuyer) {

        token = IERC721(_token);
        optionBuyer = OptionBuyer(_optionbuyer);

    }

    function deposit(uint tokenId) external {

        token.safeTransferFrom(msg.sender, address(this), tokenId);
        token.approve(address(optionBuyer), tokenId);
        optionBuyer.deposit(tokenId);

    }

    function withdraw(uint tokenId) external {

        optionBuyer.withdraw(tokenId);
        token.safeTransferFrom(address(this), msg.sender, tokenId);

    }

}
